import React, { useState } from "react";

function TableComponent({ results }) {

  const rowItems = results.items;
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // Number of items per page
  
  
  // Calculate the index of the first and last item for the current page
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  
  // Slice the results array to get the current page items
  
   const currentItems = rowItems.slice(indexOfFirstItem, indexOfLastItem);
  
  // Calculate the total number of pages
  const totalPages = Math.ceil(rowItems.length / itemsPerPage);

  // Handle page change
  const paginate = (pageNumber) => setCurrentPage(pageNumber);


  if (!results || rowItems.length === 0) { return ( <div className="no-results-message"> <p>No Product match</p> </div> ); }

  return (
    <div>
      <table className="table-container">
        <thead>
          <tr className="table-header">
            <th>Image</th>
            <th>Name</th>
            <th>Gross Price</th>
            <th>Gross Unit Price</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map((item, index) => (
            <tr className="table-row" key={index}>
              {/* Image column */}
              <td>
                <img style={{ width: "50px", height: "50px", objectFit: "contain" }}  src={item.thumbnail.url} alt={item.name} />
              </td>
              {/* Name column */}
              <td>{item.name}</td>
              {/* Gross Price column */}
              <td>{item.gross_price}</td>
              {/* Gross Unit Price column */}
              <td>{item.gross_unit_price}</td>
            </tr>
          ))}
        </tbody>
      </table>
      
   
    </div>
  );
}

export default TableComponent;
