import React, { useState } from "react";
import '../../css/SearchStyle.css';

// import { PieChart, Pie, Tooltip, Cell } from "recharts";

function App() {
  const [query, setQuery] = useState(""); // Search bar value
  const [results, setResults] = useState(null); // API results
  const [loading, setLoading] = useState(false); // Loading indicator
  const [error, setError] = useState(null); // Error handling

  const handleSearch = () => {
    setLoading(true);
    setError(null);

    // API call
    fetch(`https://localhost:7043/api/products?product=${query}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("API Error");
        }
        return response.json();
      })
      .then((data) => {
        setResults(data); // Set the entire Root object
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  const tableBody = document.getElementById("table-body");
  const tblCompo=<table>
  <thead>
      <tr>
          <th>Image</th>
          <th>Name</th>
          <th>Gross Price</th>
          <th>Gross Unit Price</th>
      </tr>
  </thead>
  <tbody id="table-body">
      
  </tbody>
</table>

  if(results!= null){
    results.forEach(item => {
      const row = document.createElement("tr");

      // Image column
      debugger
      const imgCell = document.createElement("td");
      const img = document.createElement("img");
      img.src = item.thumbnail.url;
      img.alt = item.name;
      imgCell.appendChild(img);
      row.appendChild(imgCell);

      // Name column
      const nameCell = document.createElement("td");
      nameCell.textContent = item.name;
      row.appendChild(nameCell);

      // Gross Price column
      const priceCell = document.createElement("td");
      priceCell.textContent = item.gross_price;
      row.appendChild(priceCell);

      // Gross Unit Price column
      const unitPriceCell = document.createElement("td");
      unitPriceCell.textContent = item.gross_unit_price;
      row.appendChild(unitPriceCell);

      // Append the row to the table body
      tableBody.appendChild(row);
  });
  }

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Product Search</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search for products..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{ padding: "10px", width: "300px", marginRight: "10px" }}
      />
      <button id="search" onClick={handleSearch} style={{ padding: "10px" }}>
        Search
      </button>

      {/* Loading Indicator */}
      {loading && <p>Loading...</p>}

      {/* Error Message */}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}

      {/* Results */}
      {results && (
      

        <div class="table-container">
  <div className="table-container">
  {/* Header */}
  {/* <div className="table-header">
    <div>Image</div>
    <div>Name</div>
    <div>Gross Price</div>
    <div>Gross Unit Price</div>
  </div> */}

  {/* Rows */}
  {/* <ul className="styled-table">
    {results.map((item) => (
      <li key={item.id} className="table-row">
       
        <img 
      src={item.thumbnail}
      />
        <div>{item.name}</div>
        <div>{item.gross_price}</div>
        <div>{item.gross_unit_price}</div>
      </li>
    ))}
  </ul> */}



<tblCompo/>


  
</div>








</div>

      )}
    </div>
  );
}


export default App;
