import React, { useState } from "react";
import '../../css/SearchStyle.css';
import TableComponent from '../tables/TableComponent';

// import { PieChart, Pie, Tooltip, Cell } from "recharts";

function App() {
  const [query, setQuery] = useState(""); // Search bar value
  const [results, setResults] = useState(null); // API results
  const [loading, setLoading] = useState(false); // Loading indicator
  const [error, setError] = useState(null); // Error handling
  const [page, setPage] = useState(1); // Current page
  const [pageSize, setPageSize] = useState(10); // Page size

  const handleSearch = () => {
    setLoading(true);
    setError(null);

    // API call
    fetch(`https://localhost:7043/api/products?product=${query}&page=${page}&pageSize=${pageSize}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("API Error");
        }
        return response.json();
      })
      .then((data) => {
        setResults(data); // Set the entire Root object
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

 
  useEffect(() => {
    if (query) {
      handleSearch();
    }
  }, [query, page, pageSize]);

  
  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Product Search</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search for products..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{ padding: "10px", width: "300px", marginRight: "10px" }}
      />
      <button id="search" onClick={handleSearch} style={{ padding: "10px" }}>
        Search
      </button>

      {/* Pagination Controls */}
      <div style={{ marginTop: "20px" }}>
        <label>
          Page:
          <input
            type="number"
            value={page}
            onChange={(e) => setPage(Number(e.target.value))}
            style={{ width: "50px", marginLeft: "5px", marginRight: "20px" }}
          />
        </label>
        <label>
          Page Size:
          <input
            type="number"
            value={pageSize}
            onChange={(e) => setPageSize(Number(e.target.value))}
            style={{ width: "50px", marginLeft: "5px" }}
          />
        </label>
      </div>

      {/* Loading Indicator */}
      {loading && <p>Loading...</p>}

      {/* Error Message */}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}

      {/* Results */}
      {results && <TableComponent results={results} />}

    </div>
    
  );
}


export default App;
